@article{irio_modeling_,
  langid = {english},
  title = {Modeling the {{Interference}} Caused to a {{LoRaWAN Gateway}} Due to {{Uplink Transmissions}}},
  abstract = {The performance of Low Power Wide Area Networks (LPWANs), where LoRa networks are included, has spurred much interest in recent times due to the high interest in connecting more and more devices to the so-called Internet of Things (IoT). The massive number of connected devices associated to its random spatial deployment and random network access introduce new degrees of freedom, demanding for innovative performance evaluation approaches. In this paper we study the interference caused to a LoRa gateway due to the uplink transmissions of multiple coexisting LoRa devices. By admitting an uniform spatial distribution of the devices, we show that the aggregate interference caused to a LoRa gateway follows a nonGaussian distribution and its power can be approximated by a sum of independent non-identical gamma random variables. Our work adopts a typical LoRaWAN operating scenario where the transmissions of LoRa Class A devices are affected by path-loss, shadowing and Rayleigh fading. Numerical results obtained with the modeling methodology are compared with simulation results, and the validation of the proposed model is discussed for different transmission probabilities.},
  pages = {5},
  author = {Irio, Luis and Oliveira, Rodolfo},
  file = {/home/aghiles/Aghiles/Redaction/setup/lib/iot/storage/TLWBA2M8/Irio and Oliveira - Modeling the Interference caused to a LoRaWAN Gate.pdf}
}

@article{moon_analysis_,
  langid = {english},
  title = {Analysis of {{Mood Tags}} for {{Multimedia Content Recommendation}} in {{Social Networks}}},
  abstract = {The propensity of Web information purchasers is changing from cost effectiveness that emphasizes price with performance to cost satisfaction that emphasizes purchaser's psychological satisfaction. One of the methods to improve user's cost satisfaction in recommending multimedia contents is to use mood inherent in multimedia contents. SNS services based on mood folksonomy are applications using such a method. However, in the applications based on folksonomy, the problem caused by synonyms exists. This paper suggests a cost-satisfactive multimedia contents recommendation method to solve the problem on synonym. To this end, we utilizes the value of Arousal \& Valence which express mood of multimedia content as its internal tag. A method that defines the relation between AV values of multimedia contents and AV values of mood tags is suggested by considering synonym and the correlation between them is analyzed. From the analysis, it is shown that the AV values of multimedia contents with a mood tag and its synonyms reside in the area of the mood tag in the Thayor model.},
  pages = {3},
  author = {Moon, Chang Bae and Lee, Jong Yeol and Kim, Dong-Seong and Kim, Byeong Man},
  file = {/home/aghiles/Aghiles/Redaction/setup/lib/iot/storage/BKR38HBV/Moon et al. - Analysis of Mood Tags for Multimedia Content Recom.pdf}
}

