@article{chu_interference_,
  langid = {english},
  title = {Interference {{Analysis}} of {{Directional UAV Networks}}: {{A Stochastic Geometry Approach}}},
  abstract = {We characterize the interference characteristics of directional unmanned aerial vehicle (UAV) networks based on the stochastic geometry, where each UAV is equipped with a directional antenna and is placed in three dimensional (3D) locations. In particular, the 3D location of UAVs is assumed to be uniformly distributed in a certain volume, which is modeled by Poisson point process. Given a beamwidth, we first design an ideal 3D directional antenna model with a constant gain of both main-lobe and side-lobe. Then, we investigate the aggregate interference at a typical UAV receiver from multiple UAVs. Extensive simulation results show that the aggregate interference becomes significantly decreased if the beamwidth decreases or the antenna gain of side-lobe decreases.},
  pages = {4},
  author = {Chu, Eunmi and Kim, Jong Min and Jung, Bang Chul},
  file = {/home/aghiles/Aghiles/Redaction/setup/lib/iot/storage/MF4RLYFH/Chu et al. - Interference Analysis of Directional UAV Networks.pdf}
}

@article{cahyadi_infrared_,
  langid = {english},
  title = {Infrared {{Indoor Positioning Using Invisible Beacon}}},
  abstract = {In this paper, we present an experimental demonstration of an infrared (IR) indoor positioning scheme (IPS). Unlike previous studies that employ light emitting diode (LED) and camera as the transmitter and the receiver, respectively, the proposed IPS is based on a widely available surveillance camera as the receiver and a single invisible IR LED beacon installed in smartphones as the transmitter in indoor environments. An intraframe algorithm is also proposed to provide accurate positioning on both static and moving beacon (smartphone) by minimizing the amount of ambient light interference. The proposed IPS is experimentally proven that it achieves centimeter accuracy with a mean positioning error of 6 cm.},
  pages = {5},
  author = {Cahyadi, Willy Anugrah and Chung, Yeon Ho and Adiono, Trio},
  file = {/home/aghiles/Aghiles/Redaction/setup/lib/iot/storage/FRU3JWMW/Cahyadi et al. - Infrared Indoor Positioning Using Invisible Beacon.pdf}
}

