@article{boufenneche_formal_,
  langid = {english},
  title = {Formal {{Modeling}} of {{Greedy Behavior}} in {{Secure Internet}} of {{Things Networks}}},
  abstract = {In the Internet of Things (IoT) paradigm, objects should communicate through secure wireless channels and would be able to connect to the Internet. IEEE 802.15.4e and its old versions are the de-facto standards for Wireless Sensor Networks (WSNs) based IoT. IEEE 802.15.4e and its old versions define a slotted CSMA-CA medium access control and physical layer of WSNs. The IoT may experience a multitude of malicious attacks. One critical attack that threatens the network availability is the greedy behavior. In this paper, we propose a UPPAAL model to describe greedy and sane behaviors of IoT devices. We are interested in the contention access period of the beacon-enabled mode of the slotted CSMA-CA. The developed model shows that we can assess and identify both honest and greedy devices. However, it is the first step towards thwarting this attack.},
  pages = {6},
  author = {Boufenneche, Yassine and Gharbi, Nawel and Zitouni, Rafik and George, Laurent},
  file = {/home/aghiles/Aghiles/Redaction/setup/lib/iot/storage/8JQKYA7L/Boufenneche et al. - Formal Modeling of Greedy Behavior in Secure Inter.pdf}
}

@article{back_study_,
  langid = {english},
  title = {A {{Study}} on the {{Election}} of {{Suitable Leader Vehicle}} in {{Vehicle Platooning Using}} the {{Raft Algorithm}}},
  abstract = {Existing vehicle platooning creates platooning of vehicles with the same destination, and the leader vehicle manages the platooning. Since there is no way to elect a leader vehicle for platooning management, and the leader vehicle has not changed since its initial election, it is difficult to cope with the traffic situation in real time. To complement this, it is proposed that the Raft algorithm is applied to automatically elect the appropriate leader vehicle. To verify this, the proposed platooning was analyzed by comparing them according to the platooning scenarios to confirm that flexible platooning is possible.},
  pages = {6},
  author = {Back, Jaehee and Choi, Sumin and Shin, Yongtae},
  file = {/home/aghiles/Aghiles/Redaction/setup/lib/iot/storage/TUEEMGBZ/Back et al. - A Study on the Election of Suitable Leader Vehicle.pdf},
