@article{mouawad_fast_,
  langid = {english},
  title = {Fast and {{Seamless Handover}} in {{Software Defined Vehicular Networks}}},
  abstract = {Tele-operated driving is a transition phase to the deployment of fully autonomous cars. Vehicles are supposed to communicate with a remote control station in order to exchange surrounding videos and location information. This communication is achieved through a vehicular network, and it requires low latency and high reliability. To this end, the handover management should be studied carefully. This paper devises a mobility management scheme for vehicular networks based on the Software Defined Networking (SDN) paradigm. The proposed scheme provides handover anticipation in order to guarantee a low-latency and a seamless handover. In addition, it proposes a strategy that aims at reducing control signaling overhead and network congestion. Performance analysis show that our approach reduces handover delays and losses.},
  pages = {6},
  author = {Mouawad, Nadia and Naja, Rola and Tohme, Samir},
  file = {/home/aghiles/Aghiles/Redaction/setup/lib/iot/storage/5L4SXDZZ/Mouawad et al. - Fast and Seamless Handover in Software Dened Vehi.pdf}
}

