@inproceedings{kelvin_path_2015,
  title = {Path {{Loss Prediction}} of {{Wireless Mobile Communication}} for {{Urban Areas}} of {{Imo State}}, {{South}}-{{East Region}} of {{Nigeria}} at 910 {{MHz}}},
  doi = {10.4172/2090-4886.1000117},
  abstract = {Path loss is the degradation in received power of an electromagnetic signal when it propagates through space. Path loss is due to several effects such as free space path loss, refraction, diffraction, reflection, coupling and cable loss, and absorption. Path loss depends on several factors such as type of propagations, environments, distance between the transmitter and receiver, height and location of antennas. Also, the signal from the transmitting antenna may take multiple paths (multipath) to reach the receiving side, which results in either increase or decrease of received signal level depending on the constructive or destructive interference of the multipath waves [1].},
  date = {2015},
  keywords = {PL},
  author = {Kelvin, Nnamani},
  file = {/home/aghiles/Aghiles/Redaction/setup/lib/iot/storage/9HFMITZP/Kelvin_2015_Path Loss Prediction of Wireless Mobile Communication for Urban Areas of Imo.pdf},
