@article{amano_transmission_,
  langid = {english},
  title = {Transmission {{Timing Control Method}} for {{IoT Devices Equipped}} with {{Poor Oscillator}}},
  abstract = {This paper focuses on a spectral efficiency for IoT (Internet of Things) with LPWAN (Low Power Wide Area Network). For IoT system, a packet collision causes a loss of sensor information or a waste of wireless resources due to retransmission. One of causes of the packet collision is a time jitter caused by an equipped poor oscillator, even if any packet collision avoidance is performed. Therefore, this paper proposes a method for avoiding the packet collision by controlling devices' transmission timing with expecting each packet receiving time of IoT devices at a base station. In this paper, by assuming Class A of LoRaWAN as the device's MAC protocol, the proposed method is evaluated by a computer simulation.},
  pages = {5},
