@article{khalid_sensing_,
  langid = {english},
  title = {Sensing and {{Utilization}} of {{Spectrum}} with {{Cooperation Interference}} for {{Full}}-{{Duplex Cognitive Radio Networks}}},
  abstract = {Full duplex (FD) radios in cognitive radio (CR) networks enable secondary users (SUs) to perform sensing and transmission simultaneously, and improve the utilization of spectrum. In general, cooperation between the nodes enhances the detection performance through spatial diversity. However, secondary transmission co-exists with the cooperation procedure for FD-SUs, and degrades the sensing performance when secondary transmit power is high. In this paper, we propose the sensing framework for FD-SUs in a cooperative environment, and present the analytical formulation of sensing and utilization of spectrum (UoS) performance for FD-SUs. It is demonstrated that the self-interference, secondary transmit power, PU activity, number of cooperative users, and the sensing outcomes have a significant influence on the sensing and UoS performance.},
  pages = {3},
  author = {Khalid, Waqas and Yu, Heejung},
  file = {/home/aghiles/Aghiles/Redaction/setup/lib/iot/storage/6JHBP5XH/Khalid and Yu - Sensing and Utilization of Spectrum with Cooperati.pdf},
