@article{rashid_machine_,
  langid = {english},
  title = {Machine {{Learning}} for {{Smart Energy Monitoring}} of {{Home Appliances Using IoT}}},
  abstract = {Inefficient energy use has been a major issue globally. In Malaysia, the statistics reveal that residential energy consumption has been on a steady increase due to the growing population as well as a lack of awareness within households regarding proper energy utilization that causes significant amount of energy wastage. The emergence of Internet-of-Things (IoT) is a consequence and convergence of several key technologies such as real-time analytics, machine learning, sensors and embedded systems. The application of intelligence in IoT, known as Cognitive IoT (CIoT), will enable decision making based on historical data, and automatically train, learn and troubleshoot future issues. This project proposes a smart energy monitoring system for home appliances incorporating CIoT which consists of three parts. Firstly, a Raspberry Pi-based smart plug serving as the gateway, that is able to read current data from individual home appliances, load the trained model from training server and test the verified data using the model. Secondly, Google Colab as the training server will be used to store the training data set and building the Tensorflow-based Long Short-term Memory (LSTM) model. This recurrent neural network model will forecast electricity bill and notify users if abnormal energy consumption of individual home appliances is detected. Thirdly, a dashboard using Matplotlib library where users may monitor the real-time energy consumption. The completed LSTM model demonstrates a high accuracy of more than 80\%, where a low mean squared error with train score = 0.1798 and test score = 0.1229 is determined. Furthermore, the R2 test shows a high level of goodness of fit with train score = 0.844 and test score = 0.835.},
  pages = {6},
  author = {Rashid, Rozeha A and Chin, Leon and Sarijari, M A and Ide, Teruji},
  file = {/home/aghiles/Aghiles/Redaction/setup/lib/iot/storage/4NMQVSKI/Rashid et al. - Machine Learning for Smart Energy Monitoring of Ho.pdf}
}

@article{kim_extrinsic_,
  langid = {english},
  title = {Extrinsic Calibration of a Camera-{{LIDAR}} Multi Sensor System Using a Planar Chessboard},
  abstract = {Extrinsic calibration of imaging systems containing multi-sensors have enabled unprecedented capabilities in data fusion in the fields of computer vision and robotics throughout the past few decades. In this paper, we propose a simple mutual rotation-andtranslation estimation method for a multi-sensor system containing six omnidirectional RGB cameras and a common 3D Light Detection and Ranging (LIDAR) sensor using a planar chessboard pattern. We mount the sensors on a specially designed hexagonal plate, while considering each camera-LIDAR combination as an independent multi-sensor unit. For each unit, we simultaneously capture chessboard images and their three-dimensional (3D) point data at a few different orientations. Twodimensional (2D) chess corners are reprojected into 3D space for plane fitting. RANSAC algorithm is applied on LIDAR points before they are used for plane fitting. The mutual rotation between the camera and LIDAR is calculated by aligning the normal vectors of plane fitting results. An arbitrary point from the camera plane is projected on to the LIDAR plane and the distance is minimized to estimate the mutual translation. The accuracy of this proposed method is evaluated through scene fusion experiments.},
  pages = {3},
  author = {Kim, Eung-su and Park, Soon-Yong},
  file = {/home/aghiles/Aghiles/Redaction/setup/lib/iot/storage/6R988DU4/Kim and Park - Extrinsic calibration of a camera-LIDAR multi sens.pdf}
}

