@article{kwon_face_,
  langid = {english},
  title = {Face {{Friend}}-{{Safe Adversarial Example}} on {{Face Recognition System}}},
  abstract = {Deep neural networks (DNNs) provide the excellent service on deep learning tasks such as image recognition, speech recognition, and pattern recognition. In the field of face recognition, researches using DNN have been carried out. However, face adversarial example is a serious threat in face recognition system. Face adversarial example adding a little of noise to the original face image can cause the misrecognition in the face recognition system. For example, an attacker intentionally modifies a face image with small distortion, which could cause the face recognition system to misidentify another person. It also shows the possibility of wrong recognition by another person when it is modulated by several points on the face. However, the concept of face friendsafe adversarial example can be useful in a military situation where friend and enemy forces are mixed. In the face recognition field, face friend-safe adversarial example may be needed that is correctly recognized by a friend face recognition system and misidentified by an enemy face recognition system. In this paper, we propose a face friend-safe adversarial example targeting the FaceNet face recognition system. The proposed scheme generates a face friend-safe adversarial example that is misrecognized by a enemy face recognition system but is correctly recognized by friend face recognition system with minimum distortion. For experiment, we used VGGFace2 and Labeled Faces in the Wild (LFW) as a dataset and Tensorflow as a machine learning library. Experimental results show that the proposed method has a 92.2\% attack succes rate and 91.4\% friend accuracy with only 64.22 distortion.},
  pages = {5},
  author = {Kwon, Hyun and Kwon, Ohmin and Yoon, Hyunsoo and Park, Ki-Woong},
  file = {/home/aghiles/Aghiles/Redaction/setup/lib/iot/storage/H7DKXUEB/Kwon et al. - Face Friend-Safe Adversarial Example on Face Recog.pdf},
  note = {00000}
}

@inproceedings{gur_stochastic_2014,
  title = {Stochastic {{Multi}}-{{Armed}}-{{Bandit Problem}} with {{Non}}-Stationary {{Rewards}}},
  abstract = {In a multi-armed bandit (MAB) problem a gambler needs to choose at each round of play one of K arms, each characterized by an unknown reward distribution. Reward realizations are only observed when an arm is selected, and the gambler's objective is to maximize his cumulative expected earnings over some given horizon of play T . To do this, the gambler needs to acquire information about arms (exploration) while simultaneously optimizing immediate rewards (exploitation); the price paid due to this trade off is often referred to as the regret, and the main question is how small can this price be as a function of the horizon length T . This problem has been studied extensively when the reward distributions do not change over time; an assumption that supports a sharp characterization of the regret, yet is often violated in practical settings. In this paper, we focus on a MAB formulation which allows for a broad range of temporal uncertainties in the rewards, while still maintaining mathematical tractability. We fully characterize the (regret) complexity of this class of MAB problems by establishing a direct link between the extent of allowable reward ``variation'' and the minimal achievable regret, and by establishing a connection between the adversarial and the stochastic MAB frameworks.},
  booktitle = NIPS,
